mapper.select:
SELECT /* kr.co.wisenut.mapper.CollectionMapper.chkDuplicatedCollectionName */
	   COUNT(*) AS "totalCount"
FROM   WISETM_COLLECTIONS
WHERE  COLLECTION_NAME = #{collectionName}

  AND  COLLECTION_ID != #{collectionId}

SELECT /* kr.co.wisenut.mapper.CollectionMapper.getCollectionNames */ * FROM ( SELECT WTC.COLLECTION_ID AS collectionId , WTC.COLLECTION_NAME AS collectionName , WTC.COLLECTION_TYPE AS collectionType , WTC.COLLECTION_JOB AS collectionJob , WTC.COLLECTION_DESC AS collectionDesc , WTC.COLLECTION_OWNER AS collectionOwner , WTC.FIELD_INFO AS fieldInfo , WTC.ORIGINAL_FIELD_INFO AS originalFieldInfo , WTC.DOCUMENT_FIELD AS documentField , COALESCE(WTD.DOCUMENT_COUNT, 0) AS documentCount , WTC.CRE_DT AS creDt , WTC.CRE_USER AS creUser , WTC.MOD_DT AS modDt , WTC.MOD_USER AS modUser FROM WISETM_COLLECTIONS WTC LEFT OUTER JOIN ( SELECT COLLECTION_ID , COUNT(*) AS DOCUMENT_COUNT FROM WISETM_DOCUMENTS GROUP BY COLLECTION_ID ) WTD ON WTC.COLLECTION_ID = WTD.COLLECTION_ID) WTC WHERE 1 = 1 /* AND documentCount > 0 */
AND collectionOwner = #{collectionOwner}
AND collectionJob = #{collectionJob}
ORDER BY collectionId DESC

SELECT /* kr.co.wisenut.mapper.CollectionMapper.getCollectionList */ COLLECTION_ID AS "collectionId" , COLLECTION_NAME AS "collectionName" , COLLECTION_TYPE AS "collectionType" , COLLECTION_JOB AS "collectionJob" , COLLECTION_DESC AS "collectionDesc" , COLLECTION_OWNER AS "collectionOwner" , COALESCE(FIELD_INFO, TO_CLOB(' ')) AS "fieldInfo" , COALESCE(ORIGINAL_FIELD_INFO, TO_CLOB(' ')) AS "originalFieldInfo" , COALESCE(DOCUMENT_COUNT, 0) AS "documentCount" , CASE WHEN DOCUMENT_COUNT > 0 THEN '완료' ELSE '-' END AS "uploadStatus" , TO_CHAR(CRE_DT, 'YYYY.MM.DD') AS "creDt" FROM WHERE 1 = 1
AND PAGE_ROW = #{pageRow}
AND COLLECTION_OWNER = #{collectionOwner}
AND COLLECTION_JOB = #{collectionJob}
AND COLLECTION_TYPE = #{collectionType}

mapper.insert:

	SELECT COALESCE(MAX(COLLECTION_ID),0) + 1 FROM WISETM_COLLECTIONS


INSERT /* kr.co.wisenut.textminer.collection.mapper.CollectionMapper.insertCollection */
INTO   WISETM_COLLECTIONS (COLLECTION_ID, COLLECTION_NAME, COLLECTION_TYPE, COLLECTION_JOB, COLLECTION_DESC, COLLECTION_OWNER, CRE_DT, CRE_USER, MOD_DT, MOD_USER)
VALUES ( #{collectionId}
	   , #{collectionName}
	   , #{collectionType}
	   , #{collectionJob}
	   , #{collectionDesc}
	   , #{collectionOwner}
	   , CURRENT_TIMESTAMP
	   , #{creUser}
	   , CURRENT_TIMESTAMP
	   , #{modUser}
	   )

mapper.update:
UPDATE /* kr.co.wisenut.textminer.collection.mapper.CollectionMapper.updateCollection */
	   WISETM_COLLECTIONS
SET    MOD_DT = CURRENT_TIMESTAMP
	 , MOD_USER = #{modUser}
, COLLECTION_NAME = #{collectionName}
, COLLECTION_TYPE = #{collectionType}
, COLLECTION_JOB = #{collectionJob}
, COLLECTION_DESC = #{collectionDesc}
, FIELD_INFO = #{fieldInfo}
, ORIGINAL_FIELD_INFO = #{originalFieldInfo}
, DOCUMENT_FIELD = #{documentField}
, DOCUMENT_LIST = #{documentList}
, DOCUMENT_COUNT = #{documentCount}
WHERE  COLLECTION_ID = #{collectionId}

  AND  COLLECTION_OWNER = #{collectionOwner}

mapper.delete:
DELETE /* kr.co.wisenut.textminer.collection.mapper.CollectionMapper.deleteCollection */
FROM   WISETM_COLLECTIONS
WHERE  COLLECTION_ID = #{collectionId}

  AND  COLLECTION_OWNER = #{collectionOwner}
